import React, { Component } from 'react';
import { Formik, Form, Field } from "formik";
import axios from 'axios';
class Temp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            baseUrl: 'https://v2.jokeapi.dev/joke',
           joke:{},
            count: 1,
            // joke:{'jo':'' ,'setup':'' ,'delivery':'' }
        };
        this.sendReq = this.sendReq.bind(this);
         this.blacks = this.blacks.bind(this);

    }

    sendReq(formik) {

        console.log(this.state.joke);
         console.log(formik)
        // console.log(this.state.baseUrl );



        // console.log(this.state.baseUrl);
        axios.get(this.state.baseUrl)
            .then((response)=> {this.setState({ count: response.data })
          this.setState({'joke':response.data})
            console.log(this.state.joke);

             });

    }
    blacks(formik) {
        console.log(formik)
    }
    render() {

          return (
            <div className="card-body text-white bg-dark mx-auto w-75 mt-5 border  rounded">
                <div className="row ">
                    <div className="col-lg-12 ">
                        <Formik initialValues={{
                            category: '',
                            language: 'en',
                            blacklistFlags: [],
                            type: { 'single': true, 'twopart': true },
                            conatains: '',
                            idRange: { 'from': 0, 'to': 1368 },
                            amount: '1'
                        }}
                            onSubmit={(values) => {

                             console.log(this.state.baseUrl);
                              let s='?'
                             if(values.category === 'on')
                             this.setState({baseUrl:this.state.baseUrl+`/Any`})
                             else
                             this.setState({baseUrl:this.state.baseUrl+`/${values.category}`})


                             if(values.language == 'en')
                              this.setState({baseUrl:this.state.baseUrl})
                              else{


                                //   if(this.state.baseUrl.includes('?'))
                                 this.setState({baseUrl:this.state.baseUrl+`${s}lang=${values.language}`})
                                s='&';
                                }
                               this.setState({baseUrl : this.state.baseUrl + (`&blacklistFlags=${values.blacklistFlags}`)})


                             console.log(this.state.baseUrl);
                            }
                            } >




                            {(formik) => (
                                <div>
                                    {/* {console.log(this.setState({count: formik.values.category}))} */}
                                    <div className="row mb-4 ">
                                        <div className="col-lg-12 text-center text-uppercase">
                                            <h1 className="mt-2">Joke Teller</h1>
                                        </div>
                                    </div>
                                    <Form>
                                        <div className='row mb-3'>
                                            <div className='col-4  text-start '>
                                                <label htmlFor="category">Select category/categories</label>
                                            </div>
                                            <div className='col-6 text-start p-1 border rounded'>
                                                <input type={'radio'} 
                                                onChange={formik.handleChange}
                                                id="category"
                                                name='category'/>
                                                <label>Any</label>


                                                <div>
                                                    <input type={'radio'}  id="category"
                                                    onChange={formik.handleChange}
                                                    name='category' />
                                                    <label htmlFor="custom">Custom : </label>
                                                    <select
                                                        className='w-45 ms-2'
                                                        id="category"
                                                        name="category"
                                                        type="select"
                                                        onChange={formik.handleChange}
                                                        value={formik.values.category}
                                                        onBlur={formik.handleBlur} disabled={false}
                                                    >
                                                        <option value="Programming">Programming</option>
                                                        <option value="Misc">Misc</option>
                                                        <option value="Dark">Dark</option>
                                                        <option value="Pun">Pun</option>
                                                        <option value="Spooky">Spooky</option>
                                                        <option value="Christamas">Christamas</option>
                                                    </select>
                                                </div>
                                            </div>
                                        </div>
                                        {/* {console.log(formik.values)} */}
                                        <div className='row mb-3' >
                                            <div className='col-4 text-start '>
                                                <label htmlFor="language">Select Language</label>
                                            </div>
                                            <div className='col-6 p-1 text-start border rounded'>
                                                <select className='w-45 text-center'
                                                    id="language"
                                                    name="language"
                                                    type="select"
                                                    onChange={formik.handleChange}
                                                    value={formik.values.language}
                                                    onBlur={formik.handleBlur}>
                                                    <option value="cs">cs - Czech</option>
                                                    <option value="de">de - German</option>
                                                    <option value="en">en - English</option>
                                                    <option value="es">es - Spanish</option>
                                                    <option value="fr">fr - French</option>
                                                    <option value="pt">pt - Portuguese</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className='row mb-3 '>
                                            <div className='col-4 text-start '>
                                                <label htmlFor="email">Select  Flags to Blacklist (optional)</label>
                                            </div>
                                            <div className='col-6 text-start border rounded' >
                                                <input id="blacklistFlags"

                                                    name="blacklistFlags"
                                                    onChange={formik.handleChange}
                                                    value='nsfw'
                                                    type="checkbox" />
                                                <label htmlFor="blacklistFlags">nsfw</label>



                                                <input id="blacklistFlags"
                                                    name="blacklistFlags"
                                                    onChange={formik.handleChange}
                                                    value='religious'

                                                    type="checkbox" />
                                                <label htmlFor="religious">religious</label>


                                                <input id="blacklistFlags"
                                                    name="blacklistFlags"
                                                    onChange={formik.handleChange}
                                                    value='political'
                                                    onClick={(FormikProps) => this.blacks(formik.values)}
                                                    type="checkbox" />
                                                <label htmlFor="blf-cb3">political</label>

                                                <input
                                                    id="blacklistFlags"
                                                    name="blacklistFlags"
                                                    onChange={formik.handleChange}
                                                    value="racist"
                                                    type="checkbox" />
                                                <label htmlFor="racist">racist</label>


                                                <input
                                                    id="blacklistFlags"
                                                    name="blacklistFlags"
                                                    onChange={formik.handleChange}
                                                    value='sexist'
                                                    type="checkbox" />
                                                <label htmlFor="sexist">sexist</label>

                                                <input
                                                    id="blacklistFlags"
                                                    name="blacklistFlags"
                                                    onChange={formik.handleChange}
                                                    value='explicit'
                                                   type="checkbox" />
                                                <label htmlFor="blacklistFlags">explicit</label>

                                            </div>
                                        </div>
                                        <div className='row mb-3' >
                                            <div className='col-4 text-start'>
                                                <label htmlFor="twopart">Select jock type</label>
                                            </div>
                                            <div className='col-6 text-start border rounded' style={{display:'flex'}}>
                                            <div className=' '>
                                                <input id="type.single"
                                                    name="type.single"
                                                    onChange={formik.handleChange}
                                                    value={formik.values.type.single}
                                                    checked={formik.values.type.single}
                                                    onBlur={formik.handleBlur} type="checkbox" />
                                                <label htmlFor="blf-cb1">singel</label>
                                            </div>

                                            <div className='  '>
                                                <input id="type.twopart"
                                                    name="type.twopart"
                                                    onChange={formik.handleChange}
                                                    checked={formik.values.type.twopart}
                                                    value={formik.values.type.twopart}
                                                    onBlur={formik.handleBlur} type="checkbox"  />
                                                <label htmlFor="blf-cb2">twopart</label>
                                            </div>
                                            </div>
                                        </div >


                                        <div className='row mb-3' >

                                            <div className='col-4 text-start'>
                                                <label htmlFor="email">Search for a joke that contains this search string:</label>

                                            </div>
                                            <div className='col-6 p-1 border rounded'>

                                                <input
                                                    className='w-100 '
                                                    id="conatains"
                                                    name="conatains"
                                                    type="select"
                                                    onChange={formik.handleChange}
                                                    value={formik.values.conatains}
                                                    onBlur={formik.handleBlur} type="text" placeholder='(optional)' />
                                                {formik.errors.conatains ? <div style={{ color: 'red' }}>{formik.errors.conatains}</div> : null}
                                            </div>

                                        </div>

                                        <div className='row mb-3'>
                                            {/* <div className="multiselect noselect" id="idRangeWrapper" style={{ borderColor: 'initial' }}> */}
                                            <div className='col-4 text-start'>
                                                <label htmlFor="idRange">Search for a joke in this ID range:(optional)</label>

                                            </div>

                                        <div className='col-6 text-start border rounded' style={{display:'flex',padding:'10px'}}>

                                                (optional)  From: <input id="idRange.from"

                                                    style={{ height:'25px',width:'65px'}}
                                                    name="idRange.from"
                                                    onChange={formik.handleChange}
                                                    value={formik.values.idRange.from}
                                                    onBlur={formik.handleBlur} type="number" id="idRangeInputFrom" min="0" step="1" max="1368" />


                                                To: <input id="idRange.to"

                                                    style={{ height:'25px' ,width:'65px'}}
                                                    name="idRange.to"
                                                    onChange={formik.handleChange}
                                                    value={formik.values.idRange.to}
                                                    onBlur={formik.handleBlur} type="number" id="idRangeInputTo" min="0" step="1" max="1368" />


                                            </div>
                                        </div>

                                        <div className='row mb-3' >
                                            <div className='col-4 text-start' >
                                                <label htmlFor="email">Number of jokes:</label>
                                            </div>

                                            <div className='col-6 text-start border p-1 rounded'>
                                                <input id="amount"
                                                  style={{marginTop:'10px', height:'25px' ,width:'65px'}}
                                                    name="amount"
                                                    onChange={formik.handleChange}
                                                    value={formik.values.amount}
                                                    onBlur={formik.handleBlur} type="number" id="jokesAmountInput" min="1" step="1"
                                                    onChange={formik.handleChange} max="10">
                                                </input>
                                            </div>
                                        </div>



                                    <div className='row border mt-2 w-75 me-5 ms-1 text-start rounded'>
                                        <span>URL :
                                            <label className='text-primary'>
                                                {this.state.baseUrl}
                                                {/* {`
                                            ${this.state.baseUrl} ${formik.values.category ? '/' + formik.values.category : ''}
                                            ${formik.values.language != 'en'? '?lang=' + formik.values.language : ''}

                                            `}   */}                                  
                                                       </label> 
                                        </span>
                                        <span className=' mt-2 p-1 ps-2'>
                                            <button className='me-2 rounded'>Reset Form</button>
                                            <button type="submit" className='rounded' onClick={() => this.sendReq(formik.values)}>
                                                Send Request {`>`} </button>
                                        </span>
                                    </div>
                                    </Form>
                                    {/* {console.log(formik.values)} */}

                                    {/* --------------------------------------------------Result------------------------------------------------ */}
                                    <div className='row border ms-1  bg-black rounded mt-1 '>
                                        <div className='result text-start'>
                                            <h5>{`</>`}Result</h5>



                                        </div>
                                        <hr className='border border-primary'></hr>

                                    </div>

                                    {/*----------------------------------------------End---------------------------------------------------------  */}

                                </div>




                            )}
                        </Formik>
                        {/* ==========================================Result =========================================== */}


                    </div>

                </div>



            </div>


        );
    }
}
export default Temp;
